import React, { useEffect, useState } from 'react';

import styled from 'styled-components';

import { useDispatch, useSelector } from 'react-redux';
import { Container, Grid, Image } from 'semantic-ui-react';

import { IOption, IStore, quote } from '@appmaven/redux';

const OutterWrapper = styled(Container)`
	position: relative;

	background-color: #eeeeee;
	padding-left: 50px;
	padding-right: 50px;
`;

const InnerWrapper = styled(Container)``;

const Row = styled(Grid.Row)`
	text-align: center;
`;

const Column = styled(Grid.Column)`
	text-align: center;
`;

const Title = styled.h1`
	color: #424956;
`;

const Subtitle = styled.h3`
	color: #697377;
`;

const AltTitle = styled.h3`
	color: #959da0;
	padding: 20px;
`;

const OptionImage = styled(Image)`
	padding: 10px;
	margin: auto;
`;
const OptionTitle = styled.p`
	color: #424956;
	font-weight: bold;
	padding: 20px;
	text-align: center;
`;

interface IBreakdown {
	title: string;
	subtitle: string;
	option: IOption;
}

const Quote: React.FunctionComponent<{}> = props => {
	const [counter, setCounter] = useState(0);
	const [price, setPrice] = useState<IBreakdown[]>([]);
	const dispatch = useDispatch();

	const options = useSelector<IStore, IOption[]>(
		store => store.quote.options
	);

	const title = useSelector<IStore, string>(store => store.quote.title);

	const subtitle = useSelector<IStore, string>(store => store.quote.subtitle);

	const MAX_COUNTER = 8;

	useEffect(() => {
		switch (counter) {
			case 0:
				dispatch(quote.type());
				break;
			case 1:
				dispatch(quote.login());
				break;
			case 2:
				dispatch(quote.profiles());
				break;
			case 3:
				dispatch(quote.payments());
				break;
			case 4:
				dispatch(quote.ratings());
				break;
			case 5:
				dispatch(quote.integration());
				break;
			case 6:
				dispatch(quote.ux());
				break;
			case 7:
				dispatch(quote.branding());
				break;
			case MAX_COUNTER:
				dispatch(quote.done(getPrice()));
				break;
			default:
				setPrice([]);
				setCounter(0);
				break;
		}
	}, [counter]);

	const getPrice = () => {
		let sum = 0;
		for (const item of price.slice(0, counter)) {
			sum += item.option.price;
		}
		return sum;
	};

	return (
		<OutterWrapper>
			<Grid columns={'equal'}>
				<Column>
					<AltTitle
						hidden={counter === 0}
						style={{
							textAlign: 'left'
						}}
					>
						<a
							onClick={() => {
								if (counter < MAX_COUNTER) {
									setCounter(counter - 1);
									setPrice(price.slice(0, counter - 1));
								} else {
									setCounter(0);
									setPrice([]);
								}
							}}
							style={{
								cursor: 'pointer',
								color: '#ed213a'
							}}
						>
							{counter < MAX_COUNTER ? 'back' : 'home'}
						</a>
					</AltTitle>
				</Column>
				<Column>
					<AltTitle style={{ textAlign: 'center' }}>
						App Maven Estimation
					</AltTitle>
				</Column>
				<Column>
					<AltTitle
						style={{
							textAlign: 'right',
							color: '#ed213a',
							display: counter < MAX_COUNTER ? 'block' : 'none'
						}}
					>
						£{getPrice()},00
					</AltTitle>
				</Column>
			</Grid>
			<InnerWrapper>
				<Grid columns={'equal'} textAlign={'center'}>
					<Row>
						<Title>{title}</Title>
					</Row>
					<Row>
						<Subtitle
							style={{
								fontSize: counter === MAX_COUNTER ? 32 : 16,
								color:
									counter === MAX_COUNTER
										? '#ed213a'
										: '#697377'
							}}
						>
							{subtitle}
						</Subtitle>
					</Row>
					<Row
						style={{
							display: counter === MAX_COUNTER ? 'flex' : 'none'
						}}
					>
						{price.map(breakdown => (
							<Column>
								<OptionImage
									size={'small'}
									circular={true}
									src={breakdown.option.image}
								/>
								<OptionTitle
									style={{
										padding: 0,
										fontSize: '10px'
									}}
								>
									{breakdown.option.title}
								</OptionTitle>
							</Column>
						))}
					</Row>
					<Row
						style={{
							display: counter === MAX_COUNTER ? 'block' : 'none'
						}}
					>
						<Subtitle
							style={{
								fontSize: 16
							}}
						>
							We create digital products based on needs, not
							requirements. Check out{' '}
							<a href="/portfolio">some examples of our work</a>.
							<br />
							Contact us on{' '}
							<a
								href={
									'mailto:info@appmaven.io?subject=Inquiry - estimated [£' +
									getPrice() +
									',00]'
								}
							>
								info@appmaven.io
							</a>
						</Subtitle>
					</Row>
					<Row
						style={{
							justifyContent: 'center',
							display: counter === MAX_COUNTER ? 'flex' : 'none'
						}}
					>
						<OptionImage
							size={'medium'}
							circular={true}
							src={
								'data:image/svg+xml;utf8;base64,'
							}
						/>
					</Row>
					<Row style={{ paddingTop: 50 }}>
						{options.map(option => (
							<Column key={option.title}>
								<OptionImage
									onClick={() => {
										setCounter(counter + 1);
										price.push({
											option,
											title,
											subtitle
										});
										setPrice(price);
									}}
									src={option.image}
									size={'medium'}
									circular={true}
								/>
								<OptionTitle>{option.title}</OptionTitle>
							</Column>
						))}
					</Row>
				</Grid>
			</InnerWrapper>
			<Grid columns={'equal'}>
				<Column></Column>
				<Column></Column>
				<Column
					style={{
						display: counter === MAX_COUNTER ? 'none' : 'block'
					}}
				>
					<AltTitle
						style={{
							textAlign: 'right',
							paddingTop: '70px'
						}}
					>
						{counter + 1 + ' / ' + MAX_COUNTER}
					</AltTitle>
				</Column>
			</Grid>
		</OutterWrapper>
	);
};

export default Quote;
